        int newsIndex = intent.getIntExtra("newsIndex", 0);

        WebView webView = (WebView) findViewById(R.id.webView);

        webView.getSettings().setJavaScriptEnabled(true);

        webView.setWebViewClient(new WebViewClient());

        webView.loadUrl(MainActivity.htmlList.get(newsIndex));
        webView.loadData(MainActivity.htmlList.get(newsIndex), "text/html", "UTF-8");
